headers.map:
function(doc) {
                if(doc.request_get.headers){
                    var header=doc.request_get.headers;
                    var head=[header.server,header['x-powered-by'],doc.request_get.status,header['content-type']];
                    emit(head);
                }
            }

status.map:
function(doc){
                if(doc.request_get.headers){
                    var header=doc.request_get.headers;
                    var head=[header.server,header['x-powered-by'],doc.request_get.status,header['content-type']];
                    for(var i in head){
                        if(head[i]==404){
                            emit(head[i]);
                        }
                    }
                }
            }

forms.map:
function(doc){
                if(doc.links.form){
                    for(var i in doc.links.form){
                        emit(doc.links.form[i].pathname);
                    }
                }
            }

